export class ReviewPostComponent {
  constructor(_uploadFileService, _dialog, renderer, reviewPostService, userService, router, reviewPostDestroyService, filterPostService, directLinkService, fb, progressBarService, userReactService, dateUtilsService, postReactService, followService) {
    this._uploadFileService = _uploadFileService;
    this._dialog = _dialog;
    this.renderer = renderer;
    this.reviewPostService = reviewPostService;
    this.userService = userService;
    this.router = router;
    this.reviewPostDestroyService = reviewPostDestroyService;
    this.filterPostService = filterPostService;
    this.directLinkService = directLinkService;
    this.fb = fb;
    this.progressBarService = progressBarService;
    this.userReactService = userReactService;
    this.dateUtilsService = dateUtilsService;
    this.postReactService = postReactService;
    this.followService = followService;
    this.updatedPostUser = new EventEmitter();
    /* Fake data */

    this.commentTree = [{
      user: {
        name: 'Đoàn Trung Hải',
        url: '84c1252b0128cf769639 - Copy (2).jpg'
      },
      content: 'This is a comment',
      comments: [{
        user: {
          name: 'Thanh Trúc',
          url: '84c1252b0128cf769639 - Copy (2).jpg'
        },
        content: 'This is a comment',
        comments: []
      }, {
        user: {
          name: 'Phi Hoàng',
          url: '84c1252b0128cf769639 - Copy (2).jpg'
        },
        content: 'This is a comment',
        comments: [{
          user: {
            name: 'Thiên Nguyễn',
            url: '84c1252b0128cf769639 - Copy (2).jpg'
          },
          content: 'This is a comment'
        }]
      }]
    }, {
      user: {
        name: 'Hạ Ngọc Huyền',
        url: '84c1252b0128cf769639 - Copy (2).jpg'
      },
      content: 'This is a comment',
      comments: [{
        user: {
          name: 'Huynh Gia Huy 1',
          url: '84c1252b0128cf769639 - Copy (2).jpg'
        },
        content: 'This is a comment',
        comments: []
      }]
    }];
  }

  ngOnInit() {
    this.postUser = this.reviewPost.user;
    this.user = this.userService.userBSub.value;
    /* Setup user avatar */

    if (this.postUser.avatar) {
      this.userAvatarSrc = this.directLinkService.getUserAvatar(this.postUser.avatar.name, this.postUser.avatar.ext, this.postUser.username);
    } else {
      this.userAvatarSrc = this.directLinkService.getDefaultAvatarURL(this.postUser.gender);
    }

    this.createdDate = this.dateUtilsService.dateTimeFormula(this.reviewPost.createdDate);
    /* Fetch post react */

    this.postReact$ = this.postReactService.findByReviewPostId(this.reviewPost.id);
  }

  openReviewDetail(reviewPostId) {
    this.router.navigate([`/home/review-posts/${reviewPostId}`]);
  }

  onClickVote(reviewPost, react) {
    let user = this.userService.userBSub.value;
    this.userReactService.updateUserReact(user.id, react, reviewPost.id).pipe(tap(userReact => {
      this.reviewPost.userReact = userReact;
    }), tap(_ => {
      this.postReact$ = this.postReactService.findByReviewPostId(this.reviewPost.id);
      this.userService.findByUserId(this.postUser.id).subscribe(postUser => {
        if (postUser) {
          this.postUserReputation = postUser.reputation;
          this.updatedPostUser.emit(postUser);
        }
      });
    })).subscribe();
  }

  requestFollow(userId) {
    this.followService.requestFollow(userId).subscribe(_ => {
      this.isFollowed = true;
    }, error => {});
  }

  requestUnFollow(userId) {
    this.followService.requestUnfollow(userId).subscribe(_ => {
      this.isFollowed = false;
    }, error => {});
  }

  fetchPostUser(postUserId) {
    this.postUserRefresh$ = forkJoin([this.userService.findByUserId(postUserId), this.followService.checkFollowPostUser(this.postUser.id)]).pipe(tap(response => {
      this.postUser = response[0];
      this.isFollowed = response[1];
      console.log(response);
    }));
  }
  /* ADMIN */


  onClickApprove(reviewPost) {
    reviewPost.status = APPROVE_STATUS;
  }

  onClickDeny(reviewPost) {
    reviewPost.status = DENY_STATUS;
  }

  updateReviewPostByStatus(reviewPost, status) {
    this.reviewPostService.updateByStatus(reviewPost.id, status).subscribe(response => {
      reviewPost.status = response.status;
    });
  }

